CREATE TABLE `speakers`( `id` INT(11) NOT NULL AUTO_INCREMENT,`name` VARCHAR(255),`image` VARCHAR(255),`alt_image` VARCHAR(255),`job` VARCHAR(255),`sm_twitter` VARCHAR(255),`sm_linkedin` VARCHAR(255),`sm_instagram` VARCHAR(255),`sm_facebook` VARCHAR(255),`description` TEXT,`image_company` VARCHAR(255),`alt_image_company` VARCHAR(255),`time` VARCHAR(255),`orden` VARCHAR(255), `status` ENUM('0','1'), PRIMARY KEY (`id`) );